            409 => 
            array (
                'id' => 18913,
                'capitulo_id' => 737,
                'pericopa_id' => 1328,
                'num_versiculo' => 11,
                'contenido' => 'y Jehová te guiará siempre, y en las sequías saciará tu alma y dará vigor a tus huesos; y serás como huerto de riego y como manantial cuyas aguas nunca faltan.',
                'referencia' => 'Isaías 58:11',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:23',
