            453 => 
            array (
                'id' => 18957,
                'capitulo_id' => 739,
                'pericopa_id' => 1331,
                'num_versiculo' => 20,
                'contenido' => 'No se pondrá jamás tu sol ni menguará tu luna, porque Jehová te será luz eterna, y los días de tu duelo se acabarán.',
                'referencia' => 'Isaías 60:20',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:23',
