            460 => 
            array (
                'id' => 18964,
                'capitulo_id' => 740,
                'pericopa_id' => 1332,
                'num_versiculo' => 5,
                'contenido' => 'Y habrá extranjeros, y apacentarán vuestras ovejas; y los hijos de los extranjeros serán vuestros labradores y vuestros viñadores.',
                'referencia' => 'Isaías 61:5',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:23',
                'updated_at' => '2024-03-12 15:25:23',
