            472 => 
            array (
                'id' => 18976,
                'capitulo_id' => 741,
                'pericopa_id' => 1333,
                'num_versiculo' => 6,
                'contenido' => 'Sobre tus muros, oh Jerusalén, he puesto guardias que no callarán ni de día ni de noche. Los que os acordáis de Jehová, no descanséis,',
                'referencia' => 'Isaías 62:6',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:23',
                'updated_at' => '2024-03-12 15:25:23',
            ),
            473 => 
            array (
                'id' => 18977,
                'capitulo_id' => 741,
                'pericopa_id' => 1333,
                'num_versiculo' => 7,
                'contenido' => 'ni le deis descanso, hasta que restablezca a Jerusalén y la convierta en una alabanza en la tierra.',
                'referencia' => 'Isaías 62:7',
