            array (
                'id' => 18979,
                'capitulo_id' => 741,
                'pericopa_id' => 1333,
                'num_versiculo' => 9,
                'contenido' => 'sino que los que lo cosechan lo comerán y alabarán a Jehová; y los que lo vendimian lo beberán en los atrios de mi santuario.',
                'referencia' => 'Isaías 62:9',
                'imagen' => '',
