            478 => 
            array (
                'id' => 18982,
                'capitulo_id' => 741,
                'pericopa_id' => 1333,
                'num_versiculo' => 12,
                'contenido' => 'Y los llamarán Pueblo Santo, Redimidos de Jehová; y a ti te llamarán Buscada, Ciudad No Desamparada.',
