            array (
                'id' => 19239,
                'capitulo_id' => 752,
                'pericopa_id' => 1349,
                'num_versiculo' => 4,
                'contenido' => 'No confiéis en palabras de mentira, diciendo: ¡Templo de Jehová, templo de Jehová, templo de Jehová es éste!',
                'referencia' => 'Jeremías 7:4',
