            array (
                'id' => 19249,
                'capitulo_id' => 752,
                'pericopa_id' => 1349,
                'num_versiculo' => 14,
                'contenido' => 'haré también a esta casa sobre la cual es invocado mi nombre, en la que vosotros confiáis, y a este lugar que os di a vosotros y a vuestros padres, como hice a Silo.',
                'referencia' => 'Jeremías 7:14',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:23',
                'updated_at' => '2024-03-12 15:25:23',
