            array (
                'id' => 19250,
                'capitulo_id' => 752,
                'pericopa_id' => 1349,
                'num_versiculo' => 15,
                'contenido' => 'Y os echaré de mi presencia como eché a todos vuestros hermanos, a toda la descendencia de Efraín.',
                'referencia' => 'Jeremías 7:15',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:23',
                'updated_at' => '2024-03-12 15:25:23',
