            247 => 
            array (
                'id' => 19251,
                'capitulo_id' => 752,
                'pericopa_id' => 1349,
                'num_versiculo' => 16,
                'contenido' => 'Tú, pues, no ores por este pueblo, ni levantes por ellos clamor ni oración ni intercedas ante mí, porque no te oiré.',
                'referencia' => 'Jeremías 7:16',
