            336 => 
            array (
                'id' => 19340,
                'capitulo_id' => 755,
                'pericopa_id' => 1357,
                'num_versiculo' => 23,
                'contenido' => 'Yo sé, oh Jehová, que el hombre no es señor de su camino, ni del hombre que camina es el dirigir sus pasos.',
                'referencia' => 'Jeremías 10:23',
                'imagen' => '',
                'pie_imagen' => '',
