            337 => 
            array (
                'id' => 19341,
                'capitulo_id' => 755,
                'pericopa_id' => 1357,
                'num_versiculo' => 24,
                'contenido' => 'Corrígeme, oh Jehová, pero con justicia; no con tu furor, no sea que me aniquiles.',
                'referencia' => 'Jeremías 10:24',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:23',
