            99 => 
            array (
                'id' => 19603,
                'capitulo_id' => 768,
                'pericopa_id' => 1377,
                'num_versiculo' => 3,
                'contenido' => 'Y yo recogeré al resto de mis ovejas de todas las tierras adonde las eché, y las haré volver a su redil; y crecerán y se multiplicarán.',
                'referencia' => 'Jeremías 23:3',
                'imagen' => '',
