            100 => 
            array (
                'id' => 19604,
                'capitulo_id' => 768,
                'pericopa_id' => 1377,
                'num_versiculo' => 4,
                'contenido' => 'Y pondré sobre ellas pastores que las apacienten; y no temerán más, ni se espantarán, ni faltará ninguna, dice Jehová.',
                'referencia' => 'Jeremías 23:4',
                'imagen' => '',
