            101 => 
            array (
                'id' => 19605,
                'capitulo_id' => 768,
                'pericopa_id' => 1377,
                'num_versiculo' => 5,
                'contenido' => 'He aquí que vienen días, dice Jehová, en que levantaré a David un renuevo justo, y reinará como Rey, el cual será prudente y hará juicio y justicia en la tierra.',
                'referencia' => 'Jeremías 23:5',
