            array (
                'id' => 19868,
                'capitulo_id' => 777,
                'pericopa_id' => 1389,
                'num_versiculo' => 21,
                'contenido' => 'y sacaste a tu pueblo Israel de la tierra de Egipto con señales, y con maravillas, y con mano fuerte, y con brazo extendido y con gran terror.',
