            370 => 
            array (
                'id' => 19874,
                'capitulo_id' => 777,
                'pericopa_id' => 1389,
                'num_versiculo' => 27,
                'contenido' => 'He aquí, yo soy Jehová, Dios de toda carne. ¿Acaso hay algo que sea difícil para mí?',
                'referencia' => 'Jeremías 32:27',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:24',
                'updated_at' => '2024-03-12 15:25:24',
