            371 => 
            array (
                'id' => 19875,
                'capitulo_id' => 777,
                'pericopa_id' => 1389,
                'num_versiculo' => 28,
                'contenido' => 'Por tanto, así ha dicho Jehová: He aquí que yo entrego esta ciudad en manos de los caldeos y en manos de Nabucodonosor, rey de Babilonia, y él la tomará.',
                'referencia' => 'Jeremías 32:28',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:24',
                'updated_at' => '2024-03-12 15:25:24',
