            372 => 
            array (
                'id' => 19876,
                'capitulo_id' => 777,
                'pericopa_id' => 1389,
                'num_versiculo' => 29,
                'contenido' => 'Y vendrán los caldeos que combaten contra esta ciudad, y prenderán fuego a la ciudad y la quemarán, junto con las casas en cuyas azoteas quemaron incienso a Baal y derramaron libaciones a dioses ajenos, para provocarme a ira.',
                'referencia' => 'Jeremías 32:29',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:24',
                'updated_at' => '2024-03-12 15:25:24',
