            373 => 
            array (
                'id' => 19877,
                'capitulo_id' => 777,
                'pericopa_id' => 1389,
                'num_versiculo' => 30,
                'contenido' => 'Porque los hijos de Israel y los hijos de Judá no han hecho sino lo malo delante de mis ojos desde su juventud; porque los hijos de Israel no han hecho más que provocarme a ira con la obra de sus manos, dice Jehová.',
                'referencia' => 'Jeremías 32:30',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:24',
                'updated_at' => '2024-03-12 15:25:24',
            ),
            374 => 
            array (
                'id' => 19878,
                'capitulo_id' => 777,
                'pericopa_id' => 1389,
                'num_versiculo' => 31,
                'contenido' => 'Porque para mi enojo y para mi ira me ha sido esta ciudad desde el día en que la edificaron hasta hoy, para que la haga quitar de mi presencia',
                'referencia' => 'Jeremías 32:31',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:24',
                'updated_at' => '2024-03-12 15:25:24',
