            375 => 
            array (
                'id' => 19879,
                'capitulo_id' => 777,
                'pericopa_id' => 1389,
                'num_versiculo' => 32,
                'contenido' => 'por toda la maldad de los hijos de Israel y de los hijos de Judá que han hecho para enojarme, ellos, sus reyes, sus príncipes, sus sacerdotes y sus profetas, y los hombres de Judá y los moradores de Jerusalén.',
                'referencia' => 'Jeremías 32:32',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:24',
                'updated_at' => '2024-03-12 15:25:24',
            ),
            376 => 
            array (
                'id' => 19880,
                'capitulo_id' => 777,
                'pericopa_id' => 1389,
                'num_versiculo' => 33,
                'contenido' => 'Y me volvieron la espalda y no el rostro; y cuando les enseñaba, madrugando y enseñando, no escucharon para recibir corrección,',
                'referencia' => 'Jeremías 32:33',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:24',
                'updated_at' => '2024-03-12 15:25:24',
            ),
            377 => 
            array (
                'id' => 19881,
                'capitulo_id' => 777,
                'pericopa_id' => 1389,
                'num_versiculo' => 34,
                'contenido' => 'sino que asentaron sus abominaciones en la casa sobre la cual es invocado mi nombre, profanándola.',
                'referencia' => 'Jeremías 32:34',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:24',
                'updated_at' => '2024-03-12 15:25:24',
