            383 => 
            array (
                'id' => 19887,
                'capitulo_id' => 777,
                'pericopa_id' => 1389,
                'num_versiculo' => 40,
                'contenido' => 'Y haré con ellos convenio sempiterno: que no dejaré de hacerles bien, y pondré mi temor en el corazón de ellos, para que no se aparten de mí.',
                'referencia' => 'Jeremías 32:40',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:24',
                'updated_at' => '2024-03-12 15:25:24',
