            array (
                'id' => 19888,
                'capitulo_id' => 777,
                'pericopa_id' => 1389,
                'num_versiculo' => 41,
                'contenido' => 'Y me regocijaré en ellos haciéndoles bien, y los plantaré en esta tierra en verdad, con todo mi corazón y con toda mi alma.',
                'referencia' => 'Jeremías 32:41',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:24',
                'updated_at' => '2024-03-12 15:25:24',
