            385 => 
            array (
                'id' => 19889,
                'capitulo_id' => 777,
                'pericopa_id' => 1389,
                'num_versiculo' => 42,
                'contenido' => 'Porque así ha dicho Jehová: Como traje sobre este pueblo todo este gran mal, así traeré sobre ellos todo el bien que les he prometido.',
                'referencia' => 'Jeremías 32:42',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:24',
                'updated_at' => '2024-03-12 15:25:24',
