                'id' => 19894,
                'capitulo_id' => 778,
                'pericopa_id' => 1390,
                'num_versiculo' => 3,
                'contenido' => 'Clama a mí y te responderé, y te enseñaré cosas grandes e inaccesibles que tú no conoces.',
                'referencia' => 'Jeremías 33:3',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:24',
                'updated_at' => '2024-03-12 15:25:24',
