            array (
                'id' => 19899,
                'capitulo_id' => 778,
                'pericopa_id' => 1390,
                'num_versiculo' => 8,
                'contenido' => 'Y los limpiaré de toda su iniquidad con que pecaron contra mí, y perdonaré todas sus iniquidades con que contra mí pecaron y con que transgredieron contra mí.',
                'referencia' => 'Jeremías 33:8',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:24',
                'updated_at' => '2024-03-12 15:25:24',
