            40 => 
            array (
                'id' => 20041,
                'capitulo_id' => 784,
                'pericopa_id' => 1397,
                'num_versiculo' => 2,
                'contenido' => 'Y en el undécimo año de Sedequías, en el mes cuarto, a los nueve días del mes, se abrió una brecha en la ciudad.',
                'referencia' => 'Jeremías 39:2',
