            46 => 
            array (
                'id' => 20047,
                'capitulo_id' => 784,
                'pericopa_id' => 1397,
                'num_versiculo' => 8,
                'contenido' => 'Y los caldeos prendieron fuego a la casa del rey y a las casas del pueblo, y derribaron los muros de Jerusalén.',
                'referencia' => 'Jeremías 39:8',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:24',
                'updated_at' => '2024-03-12 15:25:24',
