                'capitulo_id' => 792,
                'pericopa_id' => 1408,
                'num_versiculo' => 1,
                'contenido' => 'La palabra de Jehová que vino al profeta Jeremías acerca de los filisteos, antes que Faraón hiriese a Gaza.',
                'referencia' => 'Jeremías 47:1',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:24',
