            203 => 
            array (
                'id' => 20204,
                'capitulo_id' => 793,
                'pericopa_id' => 1409,
                'num_versiculo' => 8,
                'contenido' => 'Y vendrá el destructor a cada una de las ciudades, y ninguna ciudad escapará; también el valle será arruinado, y será destruida la llanura, como ha dicho Jehová.',
                'referencia' => 'Jeremías 48:8',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:24',
