            array (
                'id' => 20205,
                'capitulo_id' => 793,
                'pericopa_id' => 1409,
                'num_versiculo' => 9,
                'contenido' => 'Dad alas a Moab, para que se vaya volando; pues serán desoladas sus ciudades hasta no quedar en ellas morador alguno.',
                'referencia' => 'Jeremías 48:9',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:24',
                'updated_at' => '2024-03-12 15:25:24',
