            array (
                'id' => 20206,
                'capitulo_id' => 793,
                'pericopa_id' => 1409,
                'num_versiculo' => 10,
                'contenido' => 'Maldito el que hiciere engañosamente la obra de Jehová, y maldito el que retuviere su espada de la sangre.',
                'referencia' => 'Jeremías 48:10',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:24',
                'updated_at' => '2024-03-12 15:25:24',
