            76 => 
            array (
                'id' => 20577,
                'capitulo_id' => 802,
                'pericopa_id' => 1425,
                'num_versiculo' => 19,
                'contenido' => 'Mas tú, oh Jehová, permanecerás para siempre; tu trono, de generación en generación.',
