            array (
                'id' => 21097,
                'capitulo_id' => 824,
                'pericopa_id' => 1454,
                'num_versiculo' => 5,
                'contenido' => 'Las que están cerca de ti y las que están lejos se burlarán de ti, amancillada de nombre y de gran turbación.',
