            347 => 
            array (
                'id' => 21348,
                'capitulo_id' => 833,
                'pericopa_id' => 1468,
                'num_versiculo' => 2,
                'contenido' => 'Hijo de hombre, di a Faraón, rey de Egipto, y a su pueblo: ¿A quién te pareces en tu grandeza?',
                'referencia' => 'Ezequiel 31:2',
                'imagen' => '',
