                'id' => 21369,
                'capitulo_id' => 834,
                'pericopa_id' => 1469,
                'num_versiculo' => 5,
                'contenido' => 'Y pondré tu carne sobre los montes y llenaré los valles de tu altura.',
                'referencia' => 'Ezequiel 32:5',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:25',
