            193 => 
            array (
                'id' => 21694,
                'capitulo_id' => 845,
                'pericopa_id' => 1486,
                'num_versiculo' => 6,
                'contenido' => 'Y oí a alguien que me hablaba desde la casa, y un varón estaba junto a mí.',
