            array (
                'id' => 21717,
                'capitulo_id' => 846,
                'pericopa_id' => 1487,
                'num_versiculo' => 2,
                'contenido' => 'Y me dijo Jehová: Esta puerta estará cerrada; no se abrirá, ni entrará por ella hombre, porque Jehová Dios de Israel entró por ella; estará, por tanto, cerrada.',
                'referencia' => 'Ezequiel 44:2',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:26',
                'updated_at' => '2024-03-12 15:25:26',
            ),
