            array (
                'id' => 21816,
                'capitulo_id' => 849,
                'pericopa_id' => 1493,
                'num_versiculo' => 21,
                'contenido' => 'Repartiréis, pues, esta tierra entre vosotros según las tribus de Israel.',
                'referencia' => 'Ezequiel 47:21',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:26',
                'updated_at' => '2024-03-12 15:25:26',
