            386 => 
            array (
                'id' => 21887,
                'capitulo_id' => 852,
                'pericopa_id' => 1497,
                'num_versiculo' => 13,
                'contenido' => 'Y se publicó el decreto de que los sabios fueran llevados a la muerte; y buscaron a Daniel y a sus compañeros para matarlos.',
                'referencia' => 'Daniel 2:13',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:26',
                'updated_at' => '2024-03-12 15:25:26',
