            454 => 
            array (
                'id' => 21955,
                'capitulo_id' => 854,
                'pericopa_id' => 1499,
                'num_versiculo' => 2,
                'contenido' => 'Conviene que yo declare las señales y las maravillas que el Dios Altísimo ha hecho conmigo.',
                'referencia' => 'Daniel 4:2',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:26',
                'updated_at' => '2024-03-12 15:25:26',
            ),
            455 => 
            array (
                'id' => 21956,
                'capitulo_id' => 854,
                'pericopa_id' => 1499,
                'num_versiculo' => 3,
                'contenido' => '¡Cuán grandes son sus señales y cuán poderosas sus maravillas! Su reino es un reino sempiterno, y su señorío de generación en generación.',
                'referencia' => 'Daniel 4:3',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:26',
                'updated_at' => '2024-03-12 15:25:26',
