            473 => 
            array (
                'id' => 21974,
                'capitulo_id' => 854,
                'pericopa_id' => 1499,
                'num_versiculo' => 21,
                'contenido' => 'y cuyo follaje era hermoso, y su fruto abundante, y había en él alimento para todos, debajo del cual moraban las bestias del campo, y en cuyas ramas habitaban las aves del cielo,',
                'referencia' => 'Daniel 4:21',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:26',
                'updated_at' => '2024-03-12 15:25:26',
            ),
            474 => 
            array (
                'id' => 21975,
                'capitulo_id' => 854,
                'pericopa_id' => 1499,
                'num_versiculo' => 22,
                'contenido' => 'tú mismo eres, oh rey, que creciste y te hiciste poderoso, pues creció tu grandeza y ha llegado hasta el cielo, y tu dominio hasta los confines de la tierra.',
                'referencia' => 'Daniel 4:22',
                'imagen' => '',
