            63 => 
            array (
                'id' => 22064,
                'capitulo_id' => 857,
                'pericopa_id' => 1502,
                'num_versiculo' => 15,
                'contenido' => 'A mí, Daniel, se me turbó el espíritu en medio de mi cuerpo, y las visiones de mi cabeza me turbaron.',
                'referencia' => 'Daniel 7:15',
                'imagen' => '',
                'pie_imagen' => '',
