            70 => 
            array (
                'id' => 22071,
                'capitulo_id' => 857,
                'pericopa_id' => 1502,
                'num_versiculo' => 22,
                'contenido' => 'hasta que vino el Anciano de Días, y se dio el juicio a los santos del Altísimo; y llegó el tiempo, y los santos poseyeron el reino.',
                'referencia' => 'Daniel 7:22',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:26',
                'updated_at' => '2024-03-12 15:25:26',
            ),
            71 => 
            array (
                'id' => 22072,
                'capitulo_id' => 857,
                'pericopa_id' => 1502,
                'num_versiculo' => 23,
                'contenido' => 'Dijo así: La cuarta bestia será el cuarto reino en la tierra, el cual será diferente de todos los otros reinos; y a toda la tierra devorará, y la hollará y la despedazará.',
                'referencia' => 'Daniel 7:23',
