            303 => 
            array (
                'id' => 22304,
                'capitulo_id' => 869,
                'pericopa_id' => 1517,
                'num_versiculo' => 10,
                'contenido' => 'Y la soberbia de Israel testificará contra él en su propia cara; y ellos no se vuelven a Jehová su Dios ni le buscan a pesar de todo eso.',
                'referencia' => 'Oseas 7:10',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:26',
                'updated_at' => '2024-03-12 15:25:26',
            ),
            304 => 
            array (
                'id' => 22305,
                'capitulo_id' => 869,
                'pericopa_id' => 1517,
                'num_versiculo' => 11,
                'contenido' => 'Y Efraín es como una paloma incauta, sin entendimiento; llaman a Egipto, acuden a Asiria.',
                'referencia' => 'Oseas 7:11',
                'imagen' => '',
