            308 => 
            array (
                'id' => 22309,
                'capitulo_id' => 869,
                'pericopa_id' => 1517,
                'num_versiculo' => 15,
                'contenido' => 'Aunque yo adiestré y fortalecí sus brazos, contra mí traman el mal.',
                'referencia' => 'Oseas 7:15',
