            array (
                'id' => 22310,
                'capitulo_id' => 869,
                'pericopa_id' => 1517,
                'num_versiculo' => 16,
                'contenido' => 'Se volvieron, pero no al Altísimo; fueron como arco engañoso; caerán sus príncipes a espada por la insolencia de su lengua; esto será su escarnio en la tierra de Egipto.',
                'referencia' => 'Oseas 7:16',
                'imagen' => '',
