            array (
                'id' => 22318,
                'capitulo_id' => 870,
                'pericopa_id' => 1518,
                'num_versiculo' => 8,
                'contenido' => 'Será devorado Israel; pronto será entre las naciones como vasija que no contiene nada que deleite.',
                'referencia' => 'Oseas 8:8',
