            336 => 
            array (
                'id' => 22337,
                'capitulo_id' => 871,
                'pericopa_id' => 1519,
                'num_versiculo' => 13,
                'contenido' => 'Efraín, según veo, es semejante a Tiro, asentado en un lugar delicioso; pero Efraín sacará a sus hijos a la matanza.',
                'referencia' => 'Oseas 9:13',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:26',
                'updated_at' => '2024-03-12 15:25:26',
