            337 => 
            array (
                'id' => 22338,
                'capitulo_id' => 871,
                'pericopa_id' => 1519,
                'num_versiculo' => 14,
                'contenido' => 'Dales, oh Jehová, lo que les has de dar; dales matriz que aborte y pechos enjutos.',
                'referencia' => 'Oseas 9:14',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:26',
